//App.js
import React, { useEffect, useState } from "react";
import "./App.css";
/* ethers Â§âÊï∞„Çí‰Ωø„Åà„Çã„Çà„ÅÜ„Å´„Åô„Çã*/
import { ethers } from "ethers";
/* ABI„Éï„Ç°„Ç§„É´„ÇíÂê´„ÇÄWavePortal.json„Éï„Ç°„Ç§„É´„Çí„Ç§„É≥„Éù„Éº„Éà„Åô„Çã*/
import abi from "./utils/WavePortal.json";

const App = () => {
  /* „É¶„Éº„Ç∂„Éº„ÅÆ„Éë„Éñ„É™„ÉÉ„ÇØ„Ç¶„Ç©„É¨„ÉÉ„Éà„Çí‰øùÂ≠ò„Åô„Çã„Åü„ÇÅ„Å´‰ΩøÁî®„Åô„ÇãÁä∂ÊÖãÂ§âÊï∞„ÇíÂÆöÁæ© */
  const [currentAccount, setCurrentAccount] = useState("");
  /* „É¶„Éº„Ç∂„Éº„ÅÆ„É°„ÉÉ„Çª„Éº„Ç∏„Çí‰øùÂ≠ò„Åô„Çã„Åü„ÇÅ„Å´‰ΩøÁî®„Åô„ÇãÁä∂ÊÖãÂ§âÊï∞„ÇíÂÆöÁæ© */
  const [messageValue, setMessageValue] = useState("");
  /* „Åô„Åπ„Å¶„ÅÆwaves„Çí‰øùÂ≠ò„Åô„ÇãÁä∂ÊÖãÂ§âÊï∞„ÇíÂÆöÁæ© */
  const [allWaves, setAllWaves] = useState([]);
  console.log("currentAccount: ", currentAccount);
  /* „Éá„Éó„É≠„Ç§„Åï„Çå„Åü„Ç≥„É≥„Éà„É©„ÇØ„Éà„ÅÆ„Ç¢„Éâ„É¨„Çπ„Çí‰øùÊåÅ„Åô„ÇãÂ§âÊï∞„Çí‰ΩúÊàê */
  const contractAddress = "0x318225F6C9ABBC836BBF523f8f7F91eb4B9Bd42A";
  /* „Ç≥„É≥„Éà„É©„ÇØ„Éà„Åã„Çâ„Åô„Åπ„Å¶„ÅÆwaves„ÇíÂèñÂæó„Åô„Çã„É°„ÇΩ„ÉÉ„Éâ„Çí‰ΩúÊàê */
  /* ABI„ÅÆÂÜÖÂÆπ„ÇíÂèÇÁÖß„Åô„ÇãÂ§âÊï∞„Çí‰ΩúÊàê */
  const contractABI = abi.abi;

  const getAllWaves = async () => {
    const { ethereum } = window;

    try {
      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const wavePortalContract = new ethers.Contract(
          contractAddress,
          contractABI,
          signer
        );
        /* „Ç≥„É≥„Éà„É©„ÇØ„Éà„Åã„ÇâgetAllWaves„É°„ÇΩ„ÉÉ„Éâ„ÇíÂëº„Å≥Âá∫„Åô */
        const waves = await wavePortalContract.getAllWaves();
        /* UI„Å´ÂøÖË¶Å„Å™„ÅÆ„ÅØ„ÄÅ„Ç¢„Éâ„É¨„Çπ„ÄÅ„Çø„Ç§„É†„Çπ„Çø„É≥„Éó„ÄÅ„É°„ÉÉ„Çª„Éº„Ç∏„Å†„Åë„Å™„ÅÆ„Åß„ÄÅ‰ª•‰∏ã„ÅÆ„Çà„ÅÜ„Å´Ë®≠ÂÆö */
        const wavesCleaned = waves.map((wave) => {
          return {
            address: wave.waver,
            timestamp: new Date(wave.timestamp * 1000),
            message: wave.message,
          };
        });
        /* React State„Å´„Éá„Éº„Çø„ÇíÊ†ºÁ¥ç„Åô„Çã */
        setAllWaves(wavesCleaned);
      } else {
        console.log("Ethereum object doesn't exist!");
      }
    } catch (error) {
      console.log(error);
    }
  };

  /**
   * `emit`„Åï„Çå„Åü„Ç§„Éô„É≥„Éà„Çí„Éï„É≠„É≥„Éà„Ç®„É≥„Éâ„Å´ÂèçÊò†„Åï„Åõ„Çã
   */
  useEffect(() => {
    let wavePortalContract;

    const onNewWave = (from, timestamp, message) => {
      console.log("NewWave", from, timestamp, message);
      setAllWaves((prevState) => [
        ...prevState,
        {
          address: from,
          timestamp: new Date(timestamp * 1000),
          message: message,
        },
      ]);
    };

    /* NewWave„Ç§„Éô„É≥„Éà„Åå„Ç≥„É≥„Éà„É©„ÇØ„Éà„Åã„ÇâÁô∫‰ø°„Åï„Çå„Åü„Å®„Åç„Å´„ÄÅÊÉÖÂ†±„Çí„ÇíÂèó„ÅëÂèñ„Çä„Åæ„Åô */
    if (window.ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();

      wavePortalContract = new ethers.Contract(
        contractAddress,
        contractABI,
        signer
      );
      wavePortalContract.on("NewWave", onNewWave);
    }
    /*„É°„É¢„É™„É™„Éº„ÇØ„ÇíÈò≤„Åê„Åü„ÇÅ„Å´„ÄÅNewWave„ÅÆ„Ç§„Éô„É≥„Éà„ÇíËß£Èô§„Åó„Åæ„Åô*/
    return () => {
      if (wavePortalContract) {
        wavePortalContract.off("NewWave", onNewWave);
      }
    };
  }, []);

  /* window.ethereum„Å´„Ç¢„ÇØ„Çª„Çπ„Åß„Åç„Çã„Åì„Å®„ÇíÁ¢∫Ë™ç„Åô„ÇãÈñ¢Êï∞„ÇíÂÆüË£Ö */
  const checkIfWalletIsConnected = async () => {
    try {
      const { ethereum } = window;
      if (!ethereum) {
        console.log("Make sure you have MetaMask!");
        return;
      } else {
        console.log("We have the ethereum object", ethereum);
      }
      /* „É¶„Éº„Ç∂„Éº„ÅÆ„Ç¶„Ç©„É¨„ÉÉ„Éà„Å∏„ÅÆ„Ç¢„ÇØ„Çª„Çπ„ÅåË®±ÂèØ„Åï„Çå„Å¶„ÅÑ„Çã„Åã„Å©„ÅÜ„Åã„ÇíÁ¢∫Ë™ç */
      const accounts = await ethereum.request({ method: "eth_accounts" });
      if (accounts.length !== 0) {
        const account = accounts[0];
        console.log("Found an authorized account:", account);
        setCurrentAccount(account);
        getAllWaves();
      } else {
        console.log("No authorized account found");
      }
    } catch (error) {
      console.log(error);
    }
  };
  /* connectWallet„É°„ÇΩ„ÉÉ„Éâ„ÇíÂÆüË£Ö */
  const connectWallet = async () => {
    try {
      const { ethereum } = window;
      if (!ethereum) {
        alert("Get MetaMask!");
        return;
      }
      const accounts = await ethereum.request({
        method: "eth_requestAccounts",
      });
      console.log("Connected: ", accounts[0]);
      setCurrentAccount(accounts[0]);
    } catch (error) {
      console.log(error);
    }
