// App.js
import React, { useEffect, useState } from "react";
import "./App.css";
/* ethers Â§âÊï∞„Çí‰Ωø„Åà„Çã„Çà„ÅÜ„Å´„Åô„Çã*/
import { ethers } from "ethers";
import abi from "./utils/WavePortal.json";
const App = () => {
  // „É¶„Éº„Ç∂„Éº„ÅÆ„Éë„Éñ„É™„ÉÉ„ÇØ„Ç¶„Ç©„É¨„ÉÉ„Éà„Çí‰øùÂ≠ò„Åô„Çã„Åü„ÇÅ„Å´‰ΩøÁî®„Åô„ÇãÁä∂ÊÖãÂ§âÊï∞„ÇíÂÆöÁæ©„Åó„Åæ„Åô„ÄÇ
  const [currentAccount, setCurrentAccount] = useState("");

  const contractAddress = "0xE4Ed6B7A8Cb49A7C9F0c1bb678A274c03cff5Af1";
  const contractABI = abi.abi;
  console.log("currentAccount: ", currentAccount);
  // window.ethereum„Å´„Ç¢„ÇØ„Çª„Çπ„Åß„Åç„Çã„Åì„Å®„ÇíÁ¢∫Ë™ç„Åó„Åæ„Åô„ÄÇ
  const checkIfWalletIsConnected = async () => {
    try {
      const { ethereum } = window;
      if (!ethereum) {
        console.log("Make sure you have MetaMask!");
        return;
      } else {
        console.log("We have the ethereum object", ethereum);
      }
      // „É¶„Éº„Ç∂„Éº„ÅÆ„Ç¶„Ç©„É¨„ÉÉ„Éà„Å∏„ÅÆ„Ç¢„ÇØ„Çª„Çπ„ÅåË®±ÂèØ„Åï„Çå„Å¶„ÅÑ„Çã„Åã„Å©„ÅÜ„Åã„ÇíÁ¢∫Ë™ç„Åó„Åæ„Åô„ÄÇ
      const accounts = await ethereum.request({ method: "eth_accounts" });
      if (accounts.length !== 0) {
        const account = accounts[0];
        console.log("Found an authorized account:", account);
        setCurrentAccount(account);
      } else {
        console.log("No authorized account found");
      }
    } catch (error) {
      console.log(error);
    }
  };
  // connectWallet„É°„ÇΩ„ÉÉ„Éâ„ÇíÂÆüË£Ö
  const connectWallet = async () => {
    try {
      const { ethereum } = window;
      if (!ethereum) {
        alert("Get MetaMask!");
        return;
      }
      const accounts = await ethereum.request({
        method: "eth_requestAccounts",
      });
      console.log("Connected: ", accounts[0]);
      setCurrentAccount(accounts[0]);
    } catch (error) {
      console.log(error);
    }
  };
  // wave„ÅÆÂõûÊï∞„Çí„Ç´„Ç¶„É≥„Éà„Åô„ÇãÈñ¢Êï∞„ÇíÂÆüË£Ö
  const wave = async () => {
    try {
      const { ethereum } = window;
      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const wavePortalContract = new ethers.Contract(
          contractAddress,
          contractABI,
          signer
        );
        let count = await wavePortalContract.getTotalWaves();
        console.log("Retrieved total wave count...", count.toNumber());
        /*
         * „Ç≥„É≥„Éà„É©„ÇØ„Éà„Å´üëãÔºàwaveÔºâ„ÇíÊõ∏„ÅçËæº„ÇÄ„ÄÇ„Åì„Åì„Åã„Çâ...
         */
        const waveTxn = await wavePortalContract.wave();
        console.log("Mining...", waveTxn.hash);
        await waveTxn.wait();
        console.log("Mined -- ", waveTxn.hash);
        count = await wavePortalContract.getTotalWaves();
        console.log("Retrieved total wave count...", count.toNumber());
        /*-- „Åì„Åì„Åæ„Åß --*/
      } else {
        console.log("Ethereum object doesn't exist!");
      }
    } catch (error) {
      console.log(error);
    }
  };
  // WEB„Éö„Éº„Ç∏„Åå„É≠„Éº„Éâ„Åï„Çå„Åü„Å®„Åç„Å´‰∏ãË®ò„ÅÆÈñ¢Êï∞„ÇíÂÆüË°å„Åó„Åæ„Åô„ÄÇ
  useEffect(() => {
    checkIfWalletIsConnected();
  }, []);
  return (
    <div className="mainContainer">
      <div className="dataContainer">
        <div className="header">
          <span role="img" aria-label="hand-wave">
            üëã
          </span>{" "}
          WELCOME!
        </div>
        <div className="bio">
          „Ç§„Éº„Çµ„É™„Ç¢„É†„Ç¶„Ç©„É¨„ÉÉ„Éà„ÇíÊé•Á∂ö„Åó„Å¶„ÄÅ„Äå
          <span role="img" aria-label="hand-wave">
            üëã
          </span>
          (wave)„Äç„ÇíÈÄÅ„Å£„Å¶„Åè„Å†„Åï„ÅÑ
          <span role="img" aria-label="shine">
            ‚ú®
          </span>
        </div>
        {/* wave„Éú„Çø„É≥„Å´waveÈñ¢Êï∞„ÇíÈÄ£Âãï„Åï„Åõ„Çã„ÄÇ*/}
        <button className="waveButton" onClick={wave}>
          Wave at Me
        </button>
        {/* „Ç¶„Ç©„É¨„ÉÉ„Éà„Ç≥„Éç„ÇØ„Éà„ÅÆ„Éú„Çø„É≥„ÇíÂÆüË£Ö */}
        {!currentAccount && (
          <button className="waveButton" onClick={connectWallet}>
            Connect Wallet
          </button>
        )}
        {currentAccount && (
          <button className="waveButton" onClick={connectWallet}>
            Wallet Connected
          </button>
        )}
      </div>
    </div>
  );
};
export default App;
